INSERT INTO `node_type` VALUES ('client','Client','hosting_client','<strong>The person or group that runs the site.</strong> \n                This information is usually required for billing and access purposes, to ensure\n                that only certain people are able to view the information for sites they run. \n                If you do not intend on having more than one client access the system, \n                you will not need to create any additional clients for your purposes.','',1,'Client name',0,'',0,0,0,1,'client'),('package','Package','hosting_package','','',0,'',0,'',0,0,0,1,'package'),('platform','Platform','hosting_platform','<strong>The file system location on a specific web server on which to publish sites.</strong>\n                Multiple platforms can co-exist on the same web server, and need to do so for\n                upgrades to be managed, as this is accomplished by migrating the site between platforms.\n                Platforms are most commonly built for specific releases of Drupal.','',1,'Name',0,'',0,0,0,1,'platform'),('server','Server','hosting_server','<strong>The physical machine which will provide various services to your site.</strong>\n                Each server can have multiple services associated to it. To host a site you require a database service and a web service, which can either be provided by a single or multiple servers.','',1,'Host name',0,'',0,0,0,1,'server'),('site','Site','hosting_site','<strong>An instance of a hosted site.</strong>\n                It contains information relating to the site, most notably the domain name, database server \n                and platform it is being published on. A site may also have several aliases for additional\n                domains the site needs to be accessible on.','',1,'Domain name',0,'',0,0,0,1,'site'),('task','Task','hosting_task','<strong>The mechanism whereby Hostmaster keeps track of all changes that occur to the system.</strong>\n                Each task acts as a command for the back-end, and contains a full log of all changes that have occurred.\n                If a task should fail, the administrator will be notified with an explanation of exactly what went wrong,\n                and how to fix it.','',0,'',0,'',0,0,0,1,'task');
/*!40000 ALTER TABLE `node_type` ENABLE KEYS */;
